import React, { useContext, useEffect, useState } from 'react';
import { CircularProgress, FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import Swal from 'sweetalert2';
import Api from 'src/api/service';
import DashboardCard from 'src/components/shared/DashboardCard';
import AuthContext from 'src/contexto/AuthContext';
import { Timeline, TimelineConnector, TimelineContent, TimelineDot, TimelineItem, TimelineOppositeContent, TimelineSeparator } from "@mui/lab";
import jsPDF from "jspdf";
import 'jspdf-autotable';
import moment from "moment";
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import ProductPerformance from '../dashboard/components/ProductPerformance';
import Blog from '../dashboard/components/Blog';

export default function Operários() {
    const [companySelected, setCompanySelected] = useState('')
    const [userSelected, setUserSelected] = useState('')
    const [users, setUsers] = useState([])
    const { companies } = useContext(AuthContext)
    const [isLoading, setIsLoading] = useState(false)
    const pdf = new jsPDF();
    const [isHovered, setIsHovered] = useState(false);
    const [cleanigId, setCleaningId] = useState('')

    const handleMouseEnter = () => setIsHovered(true);
    const handleMouseLeave = () => setIsHovered(false);



    async function getUsersForCompany() {
        if (!companySelected) return
        setIsLoading(true)
        try {
            const { data } = await Api.get('/user/recover', { params: { companyId: companySelected } })
            setUsers(data)
        } catch (error) {
            await Swal.fire({
                icon: 'error',
                title: 'Erro ao recuperar usuários',
                showDenyButton: true,
                showCancelButton: false,
                showConfirmButton: true,
                denyButtonText: 'Cancelar',
                confirmButtonText: 'Confirmar'
            })
        }
        setIsLoading(false)
    }


    async function generatePdf(userId, name) {
        let dataForPdf;
        const confirm = await Swal.fire({
            icon: 'question',
            title: 'Gerar relatório?',
            showDenyButton: true,
            showCancelButton: false,
            showConfirmButton: true,
            denyButtonText: 'Cancelar',
            confirmButtonText: 'Confirmar'
        })
        if (!confirm.isConfirmed) return
        try {
            const { data } = await Api.get('avaliation/recover', {
                params: {
                    userId
                }
            })

            dataForPdf = data
        } catch (error) {
            await Swal.fire({
                icon: 'error',
                title: 'Não foi possível recuperar dados para gerar relatório',
                showDenyButton: false,
                showCancelButton: false,
                showConfirmButton: true,
                denyButtonText: 'Cancelar',
                confirmButtonText: 'Confirmar'
            })
        }
        if (dataForPdf.length === 0) {
            await Swal.fire({
                icon: 'info',
                title: 'Operador sem avaliações',
                showDenyButton: false,
                showCancelButton: false,
                showConfirmButton: true,
                denyButtonText: 'Cancelar',
                confirmButtonText: 'Confirmar'
            })
            return
        }
        const logoImage = 'data:image/jpeg;base64,'; // Substitua com o URL da sua imagem
        pdf.addImage(logoImage, 'PNG', 15, 15, 30, 30);

        pdf.setFontSize(12);
        pdf.text(`Intermedium`, 60, 20);

        pdf.setFontSize(10);
        pdf.text('Endereço: R. São José, 40 - Conjunto - Jardim Bandeirantes, Maracanaú - CE, 61934-070', 60, 25)

        pdf.setFontSize(10);
        pdf.text('Telefone: (85) 3031-4510', 60, 30)

        const date = moment(new Date());
        const dateAutl = date.format("DD-MM-YYYY");
        pdf.setFontSize(10);
        pdf.text(`Relatório de: ${name} - ${dateAutl}`, 15, 55);

        const headers = [['Data', 'Ambiente', 'Avaliação', 'EPIs', 'Comentário']];

        const datas = dataForPdf.map((item) => {

            const equipaments = item.EquipmentsOfAvaliation.map((equip) => equip.equipament.name).join(', ');
            const dateObject = moment(new Date());
            const currentDate = dateObject.format("DD-MM-YYYY");
            return [
                `${currentDate}`, item.Cleaning[0] ? item.Cleaning[0].Place.name : "Erro", item.status, equipaments ? equipaments : "Sem agravante", item.observation ? item.observation : "Sem observação"
            ]
        })

        pdf.autoTable({
            startY: 60,
            head: headers,
            body: datas,
        });

        pdf.setFontSize(8);
        pdf.text('© 2023 Any Software - Todos os direiros reservados.', 20, pdf.internal.pageSize.height - 10);
        const dateObject = moment(new Date());
        const currentDate = dateObject.format("DD-MM-YYYY");
        pdf.save(`${name} - ${currentDate}.pdf`);
    }


    useEffect(() => {
        getUsersForCompany()
    }, [companySelected])
    return (
        <DashboardCard background="#f2f2f2" title="Operários" action={<FormControl sx={{ m: 1, minWidth: 120 }} >
            <InputLabel >Empresa</InputLabel>
            <Select
                value={companySelected}
                label="Age"
                onChange={(company) => setCompanySelected(company.target.value)}

            >
                {companies.map((company) => {
                    return (

                        <MenuItem key={company.id} value={company.id} >{company.name}</MenuItem>
                    )
                })}
            </Select>

        </FormControl>
        }>
            <>
                {isLoading ? <CircularProgress size={25} /> : null}
                {users.length === 0 ? (
                    <span>Selecione uma empresa</span>
                ) : (
                    users.map((item) => {
                        return (
                            <Timeline
                                onMouseEnter={handleMouseEnter}
                                onMouseLeave={handleMouseLeave}
                                style={{
                                    display: 'flex',
                                    flexDirection: 'row',
                                    alignItems: 'center',
                                    justifyContent: 'space-between',
                                    background: isHovered ? '#f0ffff' : 'transparent',
                                    width: '100%'
                                }}>
                                <TimelineItem style={{ cursor: 'pointer', alignItems: 'center', justifyContent: 'center' }}
                                    onClick={() => setUserSelected(item.id)}
                                >
                                    <TimelineOppositeContent>{item.loginHash}</TimelineOppositeContent>
                                    <TimelineSeparator>
                                        <TimelineDot color={item.deletedAt ? "error" : "success"} variant="outlined" />
                                        <TimelineConnector />
                                    </TimelineSeparator>
                                    <TimelineContent>{item.name}</TimelineContent>
                                </TimelineItem>
                                <PictureAsPdfIcon onClick={() => generatePdf(item.id, item.name)} style={{ cursor: "pointer" }} />

                            </Timeline>
                        )
                    })
                )}
            </>
            {userSelected && <ProductPerformance userId={userSelected} setCleaning={setCleaningId} />}

            {cleanigId && <Blog clear={cleanigId} />}
        </DashboardCard>
    )
}